focus.contents:
css`
  .${styles.useDefault}& {
    color: ${t.linkColor};
    text-decoration: ${t.linkHoverTextDecoration};
  }
  .${styles.useSuccess}& {
    text-decoration: ${t.linkHoverTextDecoration};
  }
  .${styles.useDanger}& {
    text-decoration: ${t.linkHoverTextDecoration};
  }
  .${styles.useGrayed}& {
    color: ${t.linkDisabledColor};
    text-decoration: ${t.linkHoverTextDecoration};
  }
`

disabled.contents:
css`
  .${styles.useDefault}& {
    color: ${t.linkDisabledColor};

    &:hover {
      color: ${t.linkDisabledColor};
    }
  }
  .${styles.useSuccess}& {
    color: ${t.linkDisabledColor};

    &:hover {
      color: ${t.linkDisabledColor};
    }
  }
  .${styles.useDanger}& {
    color: ${t.linkDisabledColor};

    &:hover {
      color: ${t.linkDisabledColor};
    }
  }
  .${styles.useGrayed}& {
    color: ${t.linkDisabledColor};

    &:hover {
      color: ${t.linkDisabledColor};
    }
  }
`
